query PhotoQuery {
// starting here is where I copied and pasted the query I created in GraphiQL.
    allContentfulPhotoGallery {
        edges {
            node {
                photos {
                    title
                    description
                    file {
                        url }
                        }
                    }
                }
            }
        }`

